from aiogram import types
from aiogram.types import ContentType, Message

from loader import dp, bot
from aiogram.dispatcher.filters.builtin import Command


@dp.message_handler(content_types=ContentType.PHOTO)
async def get_photo_id(message: Message):
    await message.reply(message.photo[-1].file_id)


@dp.message_handler(Command("about_nuwm"))
async def about_NUWM(message: types.Message):
    photo_url = "https://www.education.ua/upload/i/00001815_b.jpg"
    await message.answer("–û—Ç–∂–µ, —Ç–∏ —Ö–æ—á–µ—à –¥—ñ–∑–Ω–∞—Ç–∏—Å—è –±—ñ–ª—å—à–µ –ø—Ä–æ –ù–£–í–ì–ü? –©–æ –∂, —Ç–∏ –æ–±—Ä–∞–≤ –ø—Ä–∞–≤–∏–ª—å–Ω–∏–π –ø—É–Ω–∫—Ç –≤ –º–µ–Ω—éüòÑ\n"
                         "<b>–ù–£–í–ì–ü</b> ‚Äî —Ü–µ —Å—É—á–∞—Å–Ω—ñ —Å–µ—Ä–≤—ñ—Å–∏: –±–µ–∑–ª—ñ–º—ñ—Ç–Ω–∏–π Google-–¥–∏—Å–∫, –≤–ª–∞—Å–Ω–∏–π —ñ–Ω—Ç–µ—Ä–Ω–µ—Ç-–º–∞–≥–∞–∑–∏–Ω ¬´–ö–æ–º–æ—Ä–∞¬ª, –µ–ª–µ–∫—Ç—Ä–æ–Ω–Ω–∏–π —Ä–æ–∑–∫–ª–∞–¥,–µ–ª–µ–∫—Ç—Ä–æ–Ω–Ω–∏–π –¥–µ–∫–∞–Ω–∞—Ç, —Ü–∏—Ñ—Ä–æ–≤–∏–π —Ä–µ–ø–æ–∑–∏—Ç–æ—Ä—ñ–π, –µ–ª–µ–∫—Ç—Ä–æ–Ω–Ω–∏–π –¥–æ–∫—É–º–µ–Ω—Ç–æ–æ–±—ñ–≥, –í—ñ–∫—ñ—Å–∏—Ç–µ—Ç, –º–æ–±—ñ–ª—å–Ω–∏–π –¥–æ–¥–∞—Ç–æ–∫ ¬´–ú—ñ–π –ù–£–í–ì–ü¬ª, –µ–ª–µ–∫—Ç—Ä–æ–Ω–Ω–∏–π –∂—É—Ä–Ω–∞–ª, –≤—ñ—Ä—Ç—É–∞–ª—å–Ω–µ —Å—Ç—É–¥–º—ñ—Å—Ç–µ—á–∫–æ, —Å–∏—Å—Ç–µ–º–∞ –∞–Ω—Ç–∏–ø–ª–∞–≥—ñ–∞—Ç—É UNICHECK, –Ω–∞–≤—á–∞–ª—å–Ω–∞ –ø–ª–∞—Ç—Ñ–æ—Ä–º–∞ Moodle.\n\n"
                         "–ù–∞–≤—á–∞–Ω–Ω—è —Å—Ç—É–¥–µ–Ω—Ç—ñ–≤ –ø—Ä–æ–≤–æ–¥–∏—Ç—å—Å—è –≤ 10 –Ω–∞–≤—á–∞–ª—å–Ω–∏—Ö –∫–æ—Ä–ø—É—Å–∞—Ö, –¥–µ —Ñ—É–Ω–∫—Ü—ñ–æ–Ω—É—î –ø–æ–Ω–∞–¥ 100 —Å–ø–µ—Ü—ñ–∞–ª—ñ–∑–æ–≤–∞–Ω–∏—Ö –∞—É–¥–∏—Ç–æ—Ä—ñ–π, –∫–∞–±—ñ–Ω–µ—Ç—ñ–≤, –æ—Å–Ω–∞—â–µ–Ω–∏—Ö —Å—É—á–∞—Å–Ω–æ—é –∞—É–¥—ñ–æ–≤—ñ–∑—É–∞–ª—å–Ω–æ—é –∞–ø–∞—Ä–∞—Ç—É—Ä–æ—é, –∫–æ–º–ø‚Äô—é—Ç–µ—Ä–Ω–æ—é —Ç–µ—Ö–Ω—ñ–∫–æ—é —Ç–∞ —ñ–Ω—à–∏–º –æ–±–ª–∞–¥–Ω–∞–Ω–Ω—è–º. –í —É–Ω—ñ–≤–µ—Ä—Å–∏—Ç–µ—Ç—ñ –¥—ñ—î 15 –Ω–∞—É–∫–æ–≤–æ-–¥–æ—Å–ª—ñ–¥–Ω–∏—Ö –ª–∞–±–æ—Ä–∞—Ç–æ—Ä—ñ–π, —Å–µ—Ä–µ–¥ —è–∫–∏—Ö —î —É–Ω—ñ–∫–∞–ª—å–Ω—ñ: –≥—ñ–¥—Ä–∞–≤–ª—ñ–∫–∏, –≥—ñ–¥—Ä–æ—Ç–µ—Ö–Ω—ñ–∫–∏, –º–µ–ª—ñ–æ—Ä–∞—Ü—ñ—ó —ñ “ë—Ä—É–Ω—Ç–æ–∑–Ω–∞–≤—Å—Ç–≤–∞, —É—Å—Ç–∞–Ω–æ–≤–∫–∏ –≤–æ–¥–æ–ø–æ–¥–∞—á—ñ, –≤–æ–¥–æ–ø—ñ–¥–≥–æ—Ç–æ–≤–∫–∏ —Ç–∞ –≤–æ–¥–æ–æ—á–∏—Å—Ç–∫–∏ —Ç–æ—â–æ.\n\n"
                         "–ë—ñ–ª—å—à–µ —ñ–Ω—Ñ–æ—Ä–º–∞—Ü—ñ—ó —Ç–∏ –∑–º–æ–∂–µ—à –∑–Ω–∞–π—Ç–∏ <a href='https://nuwm.edu.ua/'>—Ç—É—Ç</a>")
    await bot.send_photo(chat_id=message.from_user.id, photo=photo_url, caption="–ü–µ—Ä—à–∏–π –∫–æ—Ä–ø—É—Å –ù–£–í–ì–ü")