from aiogram import types
from loader import dp
from aiogram.dispatcher.filters.builtin import Command


@dp.message_handler(Command("advices"))
async def advices(message: types.Message):
    await message.answer("–û—Ç–∂–µ, —Ç–∏ –ø—Ä–∏–π—à–æ–≤ —Å—é–¥–∏ –∑–∞ –ø–æ—Ä–∞–¥–æ—é —ñ —Ç–∏ —ó—Ö –æ—Ç—Ä–∏–º–∞—î—à, –∞–ª–µ –¥–ª—è –ø–æ—á–∞—Ç–∫—É, –¥–µ–∫—ñ–ª—å–∫–∞ —Å–ª—ñ–≤ –ø—Ä–æ —Ç–µ —Ö—Ç–æ —è:\n\n"
                         "–ú–µ–Ω–µ –∑–≤–∞—Ç–∏ –ú–∞–∫—Å —ñ –Ω–∞ —Å—å–æ–≥–æ–¥–Ω—ñ—à–Ω—ñ–π –¥–µ–Ω—å —è —î —Å—Ç—É–¥–µ–Ω—Ç–æ–º –¥—Ä—É–≥–æ–≥–æ –∫—É—Ä—Å—É –ù–∞—Ü—ñ–æ–Ω–∞–ª—å–Ω–æ–≥–æ –£–Ω—ñ–≤–µ—Ä—Å–∏—Ç–µ—Ç—É –í–æ–¥–Ω–æ–≥–æ –ì–æ—Å–ø–æ–¥–∞—Ä—Å—Ç–≤–∞ —Ç–∞ –ü—Ä–∏—Ä–æ–¥–æ–∫–æ—Ä–∏—Å—Ç—É–≤–∞–Ω–Ω—è."
                         " –ù–∞–≤—á–∞—é—Å—è —è –∑–∞ —Å–ø–µ—Ü—ñ–∞–ª—å–Ω—ñ—Å—Ç—é –Ü–Ω–∂–µ–Ω–µ—Ä—ñ—è –ü—Ä–æ–≥—Ä–∞–º–Ω–æ–≥–æ –ó–∞–±–µ–∑–ø–µ—á–µ–Ω–Ω—èüßë‚Äçüíª.\n\n"
                         "–ù–µ –≤–∞–∂–∫–æ –ø–æ—Ä–∞—Ö—É–≤–∞—Ç–∏ –∫–æ–ª–∏ —è —Å–∞–º –∑–¥–∞–≤–∞–≤ —Ü–µ '—Å—Ç—Ä–∞—à–Ω—é—á–µ' –ó–ù–û, —Ü–µ –±—É–ª–æ –≤ 2020 —Ä–æ—Ü—ñ —ñ —è –π–æ–≥–æ —Å–∫–ª–∞–≤ –Ω–∞ 190 –±–∞–ª—ñ–≤. –í–≤–∞–∂–∞—é —Ü–µ–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç –Ω–µ —ñ–¥–µ–∞–ª—å–Ω–∏–º, –∞–ª–µ –π –Ω–µ –ø–æ–≥–∞–Ω–∏–º."
                         "\n–¢–æ–º—É –æ—Å—å –¥–æ–≤–≥–æ–æ—á—ñ–∫—É–≤–∞–Ω—ñ –ø–æ—Ä–∞–¥–∏ –æ—Å–æ–±–∏—Å—Ç–æ –≤—ñ–¥ –º–µ–Ω–µ:\n\n"
                         "1Ô∏è‚É£ –î–ª—è –ø–æ—á–∞—Ç–∫—É –ø–æ—Ç—Ä—ñ–±–Ω–æ –ø–µ—Ä–µ—Å—Ç–∞—Ç–∏ –¥—É–º–∞—Ç–∏, —â–æ —Ü–µ–π —Ç–µ—Å—Ç —Ü–µ –Ω–∞–π–≤–∞–∂–ª–∏–≤—ñ—à–µ, —â–æ —î –≤ —Ç–≤–æ—î–º—É –∂–∏—Ç—Ç—ñ, —Ü–µ –¥–∞–ª–µ–∫–æ –Ω–µ —Ç–∞–∫üòÖ\n"
                         "2Ô∏è‚É£ –ê–ª–µ –≤—Å–µ –∂, —Ü–µ –≤–∞–∂–ª–∏–≤–∏–π –µ—Ç–∞–ø –≤ –∂–∏—Ç—Ç—ñ, —è–∫–∏–π –≤ –ø–µ–≤–Ω—ñ–π –º—ñ—Ä—ñ –≤–ø–ª–∏–Ω–µ –Ω–∞ —Ç–≤–æ—é –¥–æ–ª—é, —Ç–æ–∂ —è–∫—â–æ –±–∞–∂–∞—î—à –ø–æ—Ç—Ä–∞–ø–∏—Ç–∏ –≤ –æ–º—Ä—ñ—è–Ω–∏–π —É–Ω—ñ–≤–µ—Ä—Å–∏—Ç–µ—Ç - –ø–æ—Ç—Ä—ñ–±–Ω–æ –±–∞–≥–∞—Ç–æ –ø—Ä–∞—Ü—é–≤–∞—Ç–∏üòÆ‚Äçüí®\n"
                         "3Ô∏è‚É£ –°–∞–º –æ—Å–æ–±–∏—Å—Ç–æ –¥—É–∂–µ –≤–¥—è—á–Ω–∏–π <a href='https://www.youtube.com/c/%D0%BD%D0%B0%D0%B2%D1%81%D1%96%D0%B4%D0%B2%D1%96%D1%81%D1%82%D1%96'>—Ü—å–æ–º—É</a> –∫–∞–Ω–∞–ª—É —ñ –ê–Ω–Ω—ñ –û–≥–æ–π–∫–æ —è–∫–∞ –º–µ–Ω–µ –≥–æ—Ç—É–≤–∞–ª–∞ –¥–æ –ó–ù–û —á–µ—Ä–µ–∑ —Å–≤–æ—ó –≤—ñ–¥–µ–æ—É—Ä–æ–∫–∏, –≤–æ–Ω–∏ –ø—Ä–æ—Å—Ç–æ –±–æ–º–±–µ–∑–Ω—ñüåü\n"
                         "4Ô∏è‚É£ –ù–∞–ø–µ–≤–Ω–æ –Ω–∞–π–æ—á–µ–≤–∏–¥–Ω—ñ—à–∞ –ø–æ—Ä–∞–¥–∞, –ø—Ä–æ—Å—Ç–æ –ø—Ä–æ—Ö–æ–¥—å —Ç–µ—Å—Ç–∏ –≤ –Ω–∞—à–æ–º—É –±–æ—Ç—ñ, –∞–±–æ –Ω–∞ <a href='https://zno.osvita.ua/ukrainian/'>–æ—Å—å —Ü—ñ–π</a> —Å—Ç–æ—Ä—ñ–Ω—Ü—ñ, –∑ —è–∫–æ—ó —è –≤—ñ–¥–ø–æ–≤—ñ–¥–Ω–æ —ñ —á–µ—Ä–ø–∞—é —Ü—ñ –ø–∏—Ç–∞–Ω–Ω—èüòÅ\n"
                         "5Ô∏è‚É£ –ù—É —ñ –Ω–∞–π–≥–æ–ª–æ–≤–Ω—ñ—à–µ, –ø–∞–º'—è—Ç–∞–π, —â–æ —Ç–∏ —Ä–æ–∑—É–º–Ω–∏–π/—Ä–æ–∑—É–º–Ω–∞ —ñ —Ç–æ–±—ñ –≤—Å–µ –≤–¥–∞—Å—Ç—Å—è, —è –≤ —Ç–µ–±–µ –≤—ñ—Ä—é —Ç–∞–∫ —Å–∞–º–æ —è–∫ —ñ –≤ –ø–µ—Ä–µ–º–æ–≥—É –Ω–∞—à–æ—ó –∫—Ä–∞—ó–Ω–∏ –Ω–∞–¥ –æ–∫—É–ø–∞–Ω—Ç–∞–º–∏üá∫üá¶‚ù§")

